    id_etudiant1 = #{etudiant1.idEtudiant}
    id_etudiant2 = #{etudiant2.idEtudiant}
    date_reelle_remise = #{dateReelleRemise}

WHERE id_binome = #{idBinome}
AND id_projet = #{idProjet}

mapper.select:
