mapper.update:
UPDATE binomes


		note_rapport = #{noteRapport},



		date_reelle_remise = #{dateReelleRemise}




		id_binome = #{idBinome} AND id_projet = #{idProjet}

mapper.select:
SELECT id_binome
FROM appartenir a
WHERE a.id_projet = #{idProjet} AND a.id_etudiant = #{idEtudiant}

SELECT COUNT(*)
FROM binomes
WHERE id_projet = #{idProjet}
