mapper.update:
UPDATE etudiants

    nom_etudiant = #{nomEtudiant},
    prenom_etudiant = #{prenomEtudiant},
    id_formation = #{idFormation}

WHERE id_etudiant = #{idEtudiant}

mapper.select:
SELECT e.nom_etudiant, e.prenom_etudiant
FROM etudiants e


        e.nom_etudiant = #{nomEtudiant}



        AND e.prenom_etudiant = #{prenomEtudiant}



        AND e.id_formation = #{idFormation}
